package armo_builtins
import data
# Check for images from blocklisted repos

untrustedImageRepo[msga] {
	pod := input[_]
	k := pod.kind
	k == "Pod"
	container := pod.spec.containers[i]
	path := sprintf("spec.containers[%v].image", [format_int(i, 10)])
	image := container.image
    untrusted_or_public_registries(image)

	msga := {
		"alertMessage": sprintf("image '%v' in container '%s' comes from untrusted registry", [image, container.name]),
		"packagename": "armo_builtins",
		"alertScore": 2,
		"fixPaths": [],
		"failedPaths": [path],
         "alertObject": {
			"k8sApiObjects": [pod]
		}
    }
}

untrustedImageRepo[msga] {
	wl := input[_]
	spec_template_spec_patterns := {"Deployment","ReplicaSet","DaemonSet","StatefulSet","Job"}
	spec_template_spec_patterns[wl.kind]
	container := wl.spec.template.spec.containers[i]
	path := sprintf("spec.template.spec.containers[%v].image", [format_int(i, 10)])
	image := container.image
    untrusted_or_public_registries(image)

	msga := {
		"alertMessage": sprintf("image '%v' in container '%s' comes from untrusted registry", [image, container.name]),
		"packagename": "armo_builtins",
		"alertScore": 2,
		"fixPaths": [],
		"failedPaths": [path],
         "alertObject": {
			"k8sApiObjects": [wl]
		}
    }
}

untrustedImageRepo[msga] {
	wl := input[_]
	wl.kind == "CronJob"
	container := wl.spec.jobTemplate.spec.template.spec.containers[i]
	path := sprintf("spec.jobTemplate.spec.template.spec.containers[%v].image", [format_int(i, 10)])
	image := container.image
    untrusted_or_public_registries(image)

	msga := {
		"alertMessage": sprintf("image '%v' in container '%s' comes from untrusted registry", [image, container.name]),
		"packagename": "armo_builtins",
		"alertScore": 2,
		"fixPaths": [],
		"failedPaths": [path],
        "alertObject": {
			"k8sApiObjects": [wl]
		}
    }
}

untrusted_or_public_registries(image){
	# see default-config-inputs.json for list values
	untrusted_registries := data.postureControlInputs.untrustedRegistries
	repo_prefix := untrusted_registries[_]
	startswith(image, repo_prefix)
}

untrusted_or_public_registries(image){
	# see default-config-inputs.json for list values
	public_registries := data.postureControlInputs.publicRegistries
	repo_prefix := public_registries[_]
	startswith(image, repo_prefix)
}

package armo_builtins
import data.cautils as cautils

# input: clusterrolebindings + rolebindings
# apiversion: rbac.authorization.k8s.io/v1 
# returns subjects that can exec into container

deny[msga] {
	 roles := [role |  role= input[_]; role.kind == "Role"]
    rolebindings := [rolebinding | rolebinding = input[_]; rolebinding.kind == "RoleBinding"]
    role:= roles[_]
    rolebinding := rolebindings[_]

    rule:= role.rules[_]
	can_exec_to_pod_resource(rule)
	can_exec_to_pod_verb(rule)

	rolebinding.roleRef.kind == "Role"
	rolebinding.roleRef.name == role.metadata.name

   	subject := rolebinding.subjects[i]
    path := sprintf("subjects[%v]", [format_int(i, 10)])

	msga := {
		"alertMessage": sprintf("the following %v: %v, can exec into  containers", [subject.kind, subject.name]),
		"alertScore": 9,
		"failedPaths": [path],
		"packagename": "armo_builtins",
		"alertObject": {
			"k8sApiObjects": [role, rolebinding],
			"externalObjects": {
				"subject" : [subject]
			}
		}
	}
}


