package armo_builtins

# Check if encryption in etcd in enabled for AKS
deny[msga] {
	cluster_config := input[_]
	cluster_config.apiVersion == "management.azure.com/v1"
	cluster_config.kind == "ClusterDescribe"
    cluster_config.metadata.provider == "aks"	
	config = cluster_config.data

	not isEncryptedAKS(config)

	msga := {
		"alertMessage": "etcd/secret encryption is not enabled",
		"alertScore": 3,
		"packagename": "armo_builtins",
		"failedPaths": [],
		"fixPaths": [],
		"fixCommand": "az aks nodepool add --name hostencrypt --cluster-name <myAKSCluster> --resource-group <myResourceGroup> -s Standard_DS2_v2 -l <myRegion> --enable-encryption-at-host",
		"alertObject": {
            "externalObjects": cluster_config
		}
	}
}


# Check if encryption in etcd in enabled for EKS
deny[msga] {
	cluster_config := input[_]
	cluster_config.apiVersion == "eks.amazonaws.com/v1"
	cluster_config.kind == "ClusterDescribe"
    cluster_config.metadata.provider == "eks"	
	config = cluster_config.data

	is_not_encrypted_EKS(config)


	msga := {
		"alertMessage": "etcd/secret encryption is not enabled",
		"alertScore": 3,
		"packagename": "armo_builtins",
		"failedPaths": [],
		"fixPaths": [],
		"fixCommand": "eksctl utils enable-secrets-encryption --cluster=<cluster> --key-arn=arn:aws:kms:<cluster_region>:<account>:key/<key> --region=<region>",
		"alertObject": {
			"k8sApiObjects": [],
            "externalObjects": cluster_config
		}
	}
}



# Check if encryption in etcd in enabled for GKE
deny[msga] {
	cluster_config := input[_]
	cluster_config.apiVersion == "container.googleapis.com/v1"
	cluster_config.kind == "ClusterDescribe"
    cluster_config.metadata.provider == "gke"	
	config := cluster_config.data

	not is_encrypted_GKE(config)


	msga := {
		"alertMessage": "etcd/secret encryption is not enabled",
		"alertScore": 3,
		"packagename": "armo_builtins",
		"reviewPaths": ["data.database_encryption.state"],
		"failedPaths": ["data.database_encryption.state"],
		"fixPaths": [],
		"fixCommand": "gcloud container clusters update <cluster_name> --region=<compute_region> --database-encryption-key=<key_project_id>/locations/<location>/keyRings/<ring_name>/cryptoKeys/<key_name> --project=<cluster_project_id>",
		"alertObject": {
			"k8sApiObjects": [],
            "externalObjects": cluster_config
		}
	}
}

is_encrypted_GKE(config) {
	 config.database_encryption.state == "1"
}
is_encrypted_GKE(config) {
	 config.database_encryption.state == "ENCRYPTED"
}

is_not_encrypted_EKS(cluster_config) {
	encryptionConfig := cluster_config.Cluster.EncryptionConfig[_]
    goodResources := [resource  | resource =   cluster_config.Cluster.EncryptionConfig.Resources[_]; resource == "secrets"]
	count(goodResources) == 0
}

is_not_encrypted_EKS(cluster_config) {
	cluster_config.Cluster.EncryptionConfig == null
}

is_not_encrypted_EKS(cluster_config) {
	count(cluster_config.Cluster.EncryptionConfig) == 0
}

is_not_encrypted_EKS(cluster_config) {
	encryptionConfig := cluster_config.Cluster.EncryptionConfig[_]
    count(encryptionConfig.Resources) == 0
}

isEncryptedAKS(cluster_config) {
	cluster_config.properties.agentPoolProfiles.enableEncryptionAtHost == true
}

package armo_builtins

import future.keywords.every
import future.keywords.in

# =============================== GKE ===============================
# Check if audit logs is enabled for GKE
deny[msga] {
	cluster_config := input[_]
	cluster_config.apiVersion == "container.googleapis.com/v1"
	cluster_config.kind == "ClusterDescribe"
	cluster_config.metadata.provider == "gke"
	config := cluster_config.data

	# If enableComponents is empty, it will disable logging
	# https://cloud.google.com/kubernetes-engine/docs/reference/rest/v1beta1/projects.locations.clusters#loggingcomponentconfig
	is_logging_disabled(config)
	msga := {
		"alertMessage": "audit logs is disabled",
		"alertScore": 3,
		"packagename": "armo_builtins",
		"failedPaths": [],
		"fixPaths": [],
		"fixCommand": "",
		"alertObject": {
			"k8sApiObjects": [],
			"externalObjects": cluster_config,
		},
	}
}

is_logging_disabled(cluster_config) {
	not cluster_config.logging_config.component_config.enable_components
}

is_logging_disabled(cluster_config) {
	cluster_config.logging_config.component_config.enable_components
	count(cluster_config.logging_config.component_config.enable_components) == 0
}

# =============================== EKS ===============================
# Check if audit logs is enabled for EKS
deny[msga] {
	cluster_config := input[_]
	cluster_config.apiVersion == "eks.amazonaws.com/v1"
	cluster_config.kind == "ClusterDescribe"
	cluster_config.metadata.provider == "eks"
	config := cluster_config.data

	# logSetup is an object representing the enabled or disabled Kubernetes control plane logs for your cluster.
	# types - available cluster control plane log types
	# https://docs.aws.amazon.com/eks/latest/APIReference/API_LogSetup.html
	logging_types := {"api", "audit", "authenticator", "controllerManager", "scheduler"}
	logSetups = config.Cluster.Logging.ClusterLogging
	not all_auditlogs_enabled(logSetups, logging_types)

	msga := {
		"alertMessage": "audit logs is disabled",
		"alertScore": 3,
		"packagename": "armo_builtins",
		"failedPaths": [],
		"fixCommand": "aws eks update-cluster-config --region '${REGION_CODE}' --name '${CLUSTER_NAME}' --logging '{'clusterLogging':[{'types':['api','audit','authenticator','controllerManager','scheduler'],'enabled':true}]}'",
		"fixPaths": [],
		"alertObject": {
			"k8sApiObjects": [],
			"externalObjects": cluster_config,
		},
	}
}

all_auditlogs_enabled(logSetups, types) {
	every type in types {
		auditlogs_enabled(logSetups, type)
	}
}

auditlogs_enabled(logSetups, type) {
	logSetup := logSetups[_]
	logSetup.Enabled == true
	type in logSetup.Types
}

package armo_builtins

import data.cautils

# Fails if pod has insecure-port flag enabled
deny[msga] {
    pod := input[_]
    pod.kind == "Pod"
	contains(pod.metadata.name, "kube-apiserver")
    container := pod.spec.containers[i]
	path = is_insecure_port_flag(container, i)
	msga := {
		"alertMessage": sprintf("The API server container: %v has insecure-port flag enabled", [ container.name]),
		"packagename": "armo_builtins",
		"alertScore": 7,
		"reviewPaths": [path],
		"failedPaths": [path],
		"fixPaths": [],
		"alertObject": {
			"k8sApiObjects": [pod]
		}
	}
}

is_insecure_port_flag(container, i) = path {
	command := container.command[j]
	contains(command, "--insecure-port=1")
	path := sprintf("spec.containers[%v].command[%v]", [format_int(i, 10), format_int(j, 10)])
}

