get_flag_values(cmd) = {"origin": origin, "values": values} {
	re := " ?--enable-admission-plugins=(.+?)(?: |$)"
	matchs := regex.find_all_string_submatch_n(re, cmd, -1)
	count(matchs) == 1
	values := [val | val := split(matchs[0][1], ",")[j]; val != ""]
	origin := matchs[0][0]
