		"fixPaths": fixed_path,
		"alertObject": {
			"k8sApiObjects": [wl]
		}
	}
}


replicas_one_or_less(spec) =  [failed_path, fixPath] {
	not spec.replicas
	failed_path = ""
	fixPath = {"path": "spec.replicas", "value": "YOUR_VALUE"}
}

replicas_one_or_less(spec) =  [failed_path, fixPath] {
	spec.replicas == 1
	failed_path = "spec.replicas"
	fixPath = ""
}

 get_failed_path(paths) = [paths[0]] {
	paths[0] != ""
} else = []


get_fixed_path(paths) = [paths[1]] {
	paths[1] != ""
} else = []

