# helpers

# Compare according to unit - max
compare_max(max, given) {
	endswith(max, "Mi")
	endswith(given, "Mi")
	split_max :=  split(max, "Mi")[0]
	split_given :=  split(given, "Mi")[0]
	split_given > split_max
}

compare_max(max, given) {
	endswith(max, "M")
	endswith(given, "M")
	split_max :=  split(max, "M")[0]
	split_given :=  split(given, "M")[0]
	split_given > split_max
}

